'use strict';

class Cli {
    
    constructor() {
        
        this.excludedArgs = [
            'node_modules/cli/test/',
        ];
        
    }
    
    removeExcludedArgs(args) {
    
        let self = this;
        
        args = args.slice(2).filter((value) => { 
            return self.excludedArgs.indexOf(value) === -1 ? true : false; 
        });
        
        return args;
        
    }
    
    args(key) {

        let args = this.removeExcludedArgs(process.argv);
    
        if(args.length == 0) return null;
    
        if(key !== undefined) {
            
            if(args[key]) return args[key];
    
            return null;
    
        }
    
        return args;
        
    }
    
}

module.exports = new Cli();
