'use strict';

function Cli() {
    
    this.excludedArgs = [
        'node_modules/cli/test/',
    ];

}

Cli.prototype.removeExcludedArgs = function(args) {
    
    var self = this;
    
    args = args.slice(2).filter(function(value) {
        
        return self.excludedArgs.indexOf(value) === -1 ? true : false;
        
    });
    
    return args;
    
}

Cli.prototype.args = function(key) {

    var args = this.removeExcludedArgs(process.argv);

    if(args.length == 0) return null;

    if(key !== undefined) {
        
        if(args[key]) return args[key];

        return null;

    }

    return args;
    
}

module.exports = new Cli();
