'use strict';

let assert = require('assert');
let cli = require('cli');
let exec = require( 'child_process' ).exec;

describe("Cli", () => {
    
    describe(".args", () => {
        
        it('should return "null" when without passed argument', (done) => {
            exec(cli, (err, stdout, stderr) => {
                assert.equal(null, cli.args());
                done();
            });
        });
        
        it('should return "null" when passed 0', (done) => {
            exec(cli, (err, stdout, stderr) => {
                assert.equal(null, cli.args(0));
                done();
            });
        });
        
    });
    
});