'use strict';

var assert = require('assert');
var cli = require('cli');
var exec = require( 'child_process' ).exec;

describe("Cli", function() {
    
    describe(".args", function() {
        
        it('should return "null" when without passed argument', function(done) {
            exec(cli, function(err, stdout, stderr ) {
                assert.equal(null, cli.args());
                done();
            });
        });
        
        it('should return "null" when passed 0', function(done) {
            exec(cli, function(err, stdout, stderr ) {
                assert.equal(null, cli.args(0));
                done();
            });
        });
        
    });
    
});