'use strict';

var fs = require('fs');

var ENCONDING = 'UTF-8';

function File() {

}

File.prototype.read = function(filePath, callback) {

	fs.readFile(filePath, ENCONDING, function(err, data) {

	    if (err) {
      		callback('File does not exist');

      		return;
	    }

	    callback(null, data);
	
	});
}

module.exports = new File;
