'use strict';

let fs = require('fs');

class File {
    
    constructor() {
        
        this.enconding = "UTF-8";
        
    }
    
    read(filePath, callback) {
    
        if(filePath == undefined) return callback('File path must be defined');
        
    	fs.readFile(filePath, this.enconding, (err, data) => {
    
            if (err) return callback('File does not exist');
        
            callback(null, data);
        
        });
    }
    
    write(filePath, data, callback) {
        
        if(filePath == undefined) return callback('File path must be defined');
        
        if(data == undefined) return callback('File content must be defined');
        
        fs.writeFile(filePath, data, function(err) {
            
            if(err) return callback(err);
            
            callback(null);
            
        }); 
        
    }
    
}

module.exports = new File();
