'use strict';

var fs = require('fs');

var ENCONDING = 'UTF-8';

function File() {

}

File.prototype.read = function(filePath, callback) {
    
    if(filePath == undefined) return callback('File path must be defined');
    
	fs.readFile(filePath, ENCONDING, function(err, data) {

        if (err) return callback('File does not exist');
    
        callback(null, data);
    
    });
}

module.exports = new File;
