'use strict';

var assert = require('assert');
var file = require('file');
var path = require('path');

describe('File', function() {
    
    describe('.read', function() {
        
        it('should return error "File path must be defined" when passed undefined', function(done) {
            
            file.read(undefined, function(err, data){
                assert.equal('File path must be defined', err);
                done();
            });
            
        });
        
        it('should return error "File does not exist" when passed wrong file path', function(done) {
            
            file.read('wrongfilepath.txt', function(err, data){
                assert.equal('File does not exist', err);
                done();
            });
            
        });
        
        it('should return "Test" when passed existing file path', function(done) {

            file.read(path.resolve(__dirname, 'test.txt'), function(err, data){
                assert.equal('Test', data);
                done();
            });
            
        });
        
    });
    
});