'use strict';

let assert = require('assert');
let file = require('file');
let path = require('path');

describe('File', () => {
    
    describe('.read', () => {
        
        it('should return error "File path must be defined" when passed undefined', (done) => {
            
            file.read(undefined, (err, data) => {
                assert.equal('File path must be defined', err);
                done();
            });
            
        });
        
        it('should return error "File does not exist" when passed wrong file path', (done) => {
            
            file.read('wrongfilepath.txt', (err, data) => {
                assert.equal('File does not exist', err);
                done();
            });
            
        });
        
        it('should return "Test" when passed existing file path', (done) => {

            file.read(path.resolve(__dirname, 'test.txt'), (err, data) => {
                assert.equal('Test', data);
                done();
            });
            
        });
        
    });
    
});