'use strict';

let readline = require('readline');

class Question {
    
    constructor() {
        
        this.readline = readline.createInterface({
            input: process.stdin,
            output: process.stdout,
        });
        
    }
    
    prompt(question, callback) {
        
        let self = this;
        
        this.readline.question(question, function(answer) {
            callback(null, answer);
        });
        
        return this;
        
    }
    
    close() {
        
        this.readline.close();
        
        return this;
        
    }
    
}

module.exports = new Question();
